using System;



    public class Game
    {
    public int size;
    public int frow;
    public int fcol;
    public Grid<int> grid;
    public Player player;
    public Fountain fountain;

    public Game()
    {
        int choice;
       
        Console.WriteLine("Hello, pick Grid Size: 4, 6, 8: Type the following.");
        choice = int.Parse(Console.ReadLine());

        switch (choice)
        {
            case 4:
                Console.WriteLine("4x4 Grid Created!");
                size = choice;
                frow = 0;
                fcol = 2;
                break;
           case 6:
                Console.WriteLine("6x6 Grid Created!");
                size = choice;
                frow = 4;
                fcol = 5;
                break;
            case 8:
                Console.WriteLine("8x8 Grid Created!");
                size = choice;
                frow = 2;
                fcol = 7;
                break;
            default:
                Console.WriteLine("Be sure to type the following value inputs.");
                break;
        }

        grid = new Grid<int>(size, size);
        player = new Player(grid);
        fountain = new Fountain(frow, fcol);
    }
   
